package commands

import (
	tgbotapi "github.com/go-telegram-bot-api/telegram-bot-api"
	"math/rand"
	"telegaBot/internal/service/DataBases"
	"time"
)

func (c *Commander) quotes(inputMessage *tgbotapi.Message) {
	msg := tgbotapi.NewMessage(inputMessage.Chat.ID, quotesPredictions())

	var numericKeyboard = tgbotapi.NewReplyKeyboard(
		tgbotapi.NewKeyboardButtonRow(
			tgbotapi.NewKeyboardButton("–ü–æ —Ü–∏—Ç–∞—Ç–∞–º üìñ")),
		tgbotapi.NewKeyboardButtonRow(
			tgbotapi.NewKeyboardButton("–° –∫–æ–Ω–∫—Ä–µ—Ç–Ω—ã–º –æ—Ç–≤–µ—Ç–æ–º üí≠")),
		tgbotapi.NewKeyboardButtonRow(
			tgbotapi.NewKeyboardButton("–ù–∞–∑–∞–¥ –≤ –≥–ª–∞–≤–Ω–æ–µ –º–µ–Ω—é ‚óÄÔ∏è")),
	)
	msg.ReplyMarkup = numericKeyboard
	c.bot.Send(msg)

	DataBases.UpdatePosition(inputMessage.Chat.ID, 3)
}

func quotesPredictions() string {
	// –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∏—Ä—É–µ–º –≥–µ–Ω–µ—Ä–∞—Ç–æ—Ä —Å–ª—É—á–∞–π–Ω—ã—Ö —á–∏—Å–µ–ª
	rand.Seed(time.Now().UnixNano())

	// –û–ø—Ä–µ–¥–µ–ª—è–µ–º —Å–ø–∏—Å–æ–∫ –ø—Ä–µ–¥—Å–∫–∞–∑–∞–Ω–∏–π
	predictions := []string{
		`¬´–ß–µ–º —É–º–Ω–µ–µ —á–µ–ª–æ–≤–µ–∫, —Ç–µ–º –ª–µ–≥—á–µ –æ–Ω –ø—Ä–∏–∑–Ω–∞–µ—Ç —Å–µ–±—è –¥—É—Ä–∞–∫–æ–º¬ª. –ê–ª—å–±–µ—Ä—Ç –≠–π–Ω—à—Ç–µ–π–Ω`,
		`¬´–ù–∏–∫–æ–≥–¥–∞ –Ω–µ –æ—à–∏–±–∞–µ—Ç—Å—è —Ç–æ—Ç, –∫—Ç–æ –Ω–∏—á–µ–≥–æ –Ω–µ –¥–µ–ª–∞–µ—Ç¬ª. –¢–µ–æ–¥–æ—Ä –†—É–∑–≤–µ–ª—å—Ç`,
		`¬´–ú–µ–Ω–µ–µ –≤—Å–µ–≥–æ –ø—Ä–æ—Å—Ç—ã –ª—é–¥–∏, –∂–µ–ª–∞—é—â–∏–µ –∫–∞–∑–∞—Ç—å—Å—è –ø—Ä–æ—Å—Ç—ã–º–∏¬ª. –õ–µ–≤ –ù–∏–∫–æ–ª–∞–µ–≤–∏—á –¢–æ–ª—Å—Ç–æ–π`,
		`¬´–ú—ã –Ω–∞—Ö–æ–¥–∏–º—Å—è –∑–¥–µ—Å—å, —á—Ç–æ–±—ã –≤–Ω–µ—Å—Ç–∏ —Å–≤–æ–π –≤–∫–ª–∞–¥ –≤ —ç—Ç–æ—Ç –º–∏—Ä. –ò–Ω–∞—á–µ –∑–∞—á–µ–º –º—ã –∑–¥–µ—Å—å?¬ª –°—Ç–∏–≤ –î–∂–æ–±—Å`,
		`¬´–ú–æ–¥–∞ –ø—Ä–æ—Ö–æ–¥–∏—Ç, —Å—Ç–∏–ª—å –æ—Å—Ç–∞—ë—Ç—Å—è¬ª. –ö–æ–∫–æ –®–∞–Ω–µ–ª—å`,
		`¬´–ï—Å–ª–∏ —á–µ–ª–æ–≤–µ–∫ –Ω–µ –Ω–∞—à—ë–ª, –∑–∞ —á—Ç–æ –º–æ–∂–µ—Ç —É–º–µ—Ä–µ—Ç—å, –æ–Ω –Ω–µ —Å–ø–æ—Å–æ–±–µ–Ω –∂–∏—Ç—å¬ª. –ú–∞—Ä—Ç–∏–Ω –õ—é—Ç–µ—Ä –ö–∏–Ω–≥`,
		`¬´–ú—É–∑—ã–∫–∞ –∑–∞–≤–æ–¥–∏—Ç —Å–µ—Ä–¥—Ü–∞ —Ç–∞–∫, —á—Ç–æ –ø–ª—è—à–µ—Ç –∏ –ø–æ—ë—Ç —Ç–µ–ª–æ. –ê –µ—Å—Ç—å –º—É–∑—ã–∫–∞, —Å –∫–æ—Ç–æ—Ä–æ–π —Ö–æ—á–µ—Ç—Å—è –ø–æ–¥–µ–ª–∏—Ç—å—Å—è –≤—Å–µ–º, —á—Ç–æ –Ω–∞–±–æ–ª–µ–ª–æ¬ª. –î–∂–æ–Ω –õ–µ–Ω–Ω–æ–Ω`,
		`¬´–ï—Å–ª–∏ –∫—Ç–æ-—Ç–æ –ø—Ä–∏—á–∏–Ω–∏–ª —Ç–µ–±–µ –∑–ª–æ, –Ω–µ –º—Å—Ç–∏. –°—è–¥—å –Ω–∞ –±–µ—Ä–µ–≥—É —Ä–µ–∫–∏, –∏ –≤—Å–∫–æ—Ä–µ —Ç—ã —É–≤–∏–¥–∏—à—å, –∫–∞–∫ –º–∏–º–æ —Ç–µ–±—è –ø—Ä–æ–ø–ª—ã–≤–∞–µ—Ç —Ç—Ä—É–ø —Ç–≤–æ–µ–≥–æ –≤—Ä–∞–≥–∞¬ª. –õ–∞–æ-—Ü–∑—ã`,
		`¬´–õ—É—á—à–µ –±—ã—Ç—å —Ö–æ—Ä–æ—à–∏–º —á–µ–ª–æ–≤–µ–∫–æ–º, "—Ä—É–≥–∞—é—â–∏–º—Å—è –º–∞—Ç–æ–º", —á–µ–º —Ç–∏—Ö–æ–π, –≤–æ—Å–ø–∏—Ç–∞–Ω–Ω–æ–π —Ç–≤–∞—Ä—å—é¬ª. –§–∞–∏–Ω–∞ –†–∞–Ω–µ–≤—Å–∫–∞—è`,
		`¬´–ï—Å–ª–∏ —Ç–µ–±–µ —Ç—è–∂–µ–ª–æ, –∑–Ω–∞—á–∏—Ç —Ç—ã –ø–æ–¥–Ω–∏–º–∞–µ—à—å—Å—è –≤ –≥–æ—Ä—É. –ï—Å–ª–∏ —Ç–µ–±–µ –ª–µ–≥–∫–æ, –∑–Ω–∞—á–∏—Ç —Ç—ã –ª–µ—Ç–∏—à—å –≤ –ø—Ä–æ–ø–∞—Å—Ç—å¬ª. –ì–µ–Ω—Ä–∏ –§–æ—Ä–¥`,
		`¬´–ú–æ–π —Å–ø–æ—Å–æ–± —à—É—Ç–∏—Ç—å ‚Äì —ç—Ç–æ –≥–æ–≤–æ—Ä–∏—Ç—å –ø—Ä–∞–≤–¥—É. –ù–∞ —Å–≤–µ—Ç–µ –Ω–µ—Ç –Ω–∏—á–µ–≥–æ —Å–º–µ—à–Ω–µ–µ¬ª. –ë–µ—Ä–Ω–∞—Ä–¥ –®–æ—É`,
		`¬´–ß–µ–º –±–æ–ª—å—à–µ –ª—é–±–≤–∏, –º—É–¥—Ä–æ—Å—Ç–∏, –∫—Ä–∞—Å–æ—Ç—ã, –¥–æ–±—Ä–æ—Ç—ã –≤—ã –æ—Ç–∫—Ä–æ–µ—Ç–µ –≤ —Å–∞–º–æ–º —Å–µ–±–µ, —Ç–µ–º –±–æ–ª—å—à–µ –≤—ã –∑–∞–º–µ—Ç–∏—Ç–µ –∏—Ö –≤ –æ–∫—Ä—É–∂–∞—é—â–µ–º –º–∏—Ä–µ¬ª. –ú–∞—Ç—å –¢–µ—Ä–µ–∑–∞`,
		`¬´–ï–¥–∏–Ω—Å—Ç–≤–µ–Ω–Ω—ã–π —á–µ–ª–æ–≤–µ–∫, —Å –∫–æ—Ç–æ—Ä—ã–º –≤—ã –¥–æ–ª–∂–Ω—ã —Å—Ä–∞–≤–Ω–∏–≤–∞—Ç—å —Å–µ–±—è, ‚Äì —ç—Ç–æ –≤—ã –≤ –ø—Ä–æ—à–ª–æ–º. –ò –µ–¥–∏–Ω—Å—Ç–≤–µ–Ω–Ω—ã–π —á–µ–ª–æ–≤–µ–∫, –ª—É—á—à–µ –∫–æ—Ç–æ—Ä–æ–≥–æ –≤—ã –¥–æ–ª–∂–Ω—ã –±—ã—Ç—å, ‚Äì —ç—Ç–æ –≤—ã —Å–µ–π—á–∞—Å¬ª. –ó–∏–≥–º—É–Ω–¥ –§—Ä–µ–π–¥`,
		`¬´–ù–µ–≤–æ–∑–º–æ–∂–Ω–æ—Å—Ç—å –ø–∏—Å–∞—Ç—å –¥–ª—è –º–µ–Ω—è —Ä–∞–≤–Ω–æ—Å–∏–ª—å–Ω–∞ –ø–æ–≥—Ä–µ–±–µ–Ω–∏—é –∑–∞–∂–∏–≤–æ...¬ª –ú–∏—Ö–∞–∏–ª –ë—É–ª–≥–∞–∫–æ–≤`,
		`¬´–ò—Å—Ç–æ—Ä–∏—è ‚Äì —Å–∞–º—ã–π –ª—É—á—à–∏–π —É—á–∏—Ç–µ–ª—å, —É –∫–æ—Ç–æ—Ä–æ–≥–æ —Å–∞–º—ã–µ –ø–ª–æ—Ö–∏–µ —É—á–µ–Ω–∏–∫–∏¬ª. –ò–Ω–¥–∏—Ä–∞ –ì–∞–Ω–¥–∏`,
		`¬´–î–∞–π —á–µ–ª–æ–≤–µ–∫—É –≤–ª–∞—Å—Ç—å, –∏ —Ç—ã —É–∑–Ω–∞–µ—à—å, –∫—Ç–æ –æ–Ω¬ª. –ù–∞–ø–æ–ª–µ–æ–Ω –ë–æ–Ω–∞–ø–∞—Ä—Ç`,
		`¬´–Ø–¥–µ—Ä–Ω—É—é –≤–æ–π–Ω—É –Ω–µ–≤–æ–∑–º–æ–∂–Ω–æ –≤—ã–∏–≥—Ä–∞—Ç—å¬ª. –ê–Ω–¥—Ä–µ–π –°–∞—Ö–∞—Ä–æ–≤`,
		`¬´–ü–æ—Ä–∞–∂–µ–Ω–∏–µ? –Ø –Ω–µ –ø–æ–Ω–∏–º–∞—é –∑–Ω–∞—á–µ–Ω–∏—è —ç—Ç–æ–≥–æ —Å–ª–æ–≤–∞¬ª. –ú–∞—Ä–≥–∞—Ä–µ—Ç –¢—ç—Ç—á–µ—Ä`,
		`¬´–ù–µ–∫–æ—Ç–æ—Ä—ã–µ –ª—é–¥–∏ –ø—Ä–æ–≤–æ–¥—è—Ç –∂–∏–∑–Ω—å –≤ –ø–æ–∏—Å–∫–∞—Ö –ª—é–±–≤–∏ –≤–Ω–µ –∏—Ö —Å–∞–º–∏—Ö... –ü–æ–∫–∞ –ª—é–±–æ–≤—å –≤ –º–æ—ë–º —Å–µ—Ä–¥—Ü–µ, –æ–Ω–∞ –ø–æ–≤—Å—é–¥—É¬ª. –ú–∞–π–∫–ª –î–∂–µ–∫—Å–æ–Ω`,
		`¬´–ß–µ–ª–æ–≤–µ—á–µ—Å—Ç–≤–æ –æ–±–ª–∞–¥–∞–µ—Ç –æ–¥–Ω–∏–º –ø–æ–∏—Å—Ç–∏–Ω–µ –º–æ—â–Ω—ã–º –æ—Ä—É–∂–∏–µ–º, –∏ —ç—Ç–æ —Å–º–µ—Ö¬ª. –ú–∞—Ä–∫ –¢–≤–µ–Ω`,
		`¬´–¢—Ä–µ–Ω–∏—Ä—É–π—Å—è —Å —Ç–µ–º–∏, –∫—Ç–æ —Å–∏–ª—å–Ω–µ–µ. –ù–µ —Å–¥–∞–≤–∞–π—Å—è —Ç–∞–º, –≥–¥–µ —Å–¥–∞—é—Ç—Å—è –¥—Ä—É–≥–∏–µ. –ò –ø–æ–±–µ–¥–∏—à—å —Ç–∞–º, –≥–¥–µ –ø–æ–±–µ–¥–∏—Ç—å –Ω–µ–ª—å–∑—è¬ª. –ë—Ä—é—Å –õ–∏`,
		`¬´–ö–æ–º–µ–¥–∏—è ‚Äì —ç—Ç–æ –æ—á–µ–Ω—å —Å–µ—Ä—å—ë–∑–Ω–æ–µ –¥–µ–ª–æ!¬ª –Æ—Ä–∏–π –ù–∏–∫—É–ª–∏–Ω`,
		`¬´–ë—É–¥—å—Ç–µ –º–µ–Ω–µ–µ –ª—é–±–æ–ø—ã—Ç–Ω—ã –æ –ª—é–¥—è—Ö, –Ω–æ –±–æ–ª–µ–µ –ª—é–±–æ–ø—ã—Ç–Ω—ã –æ–± –∏–¥–µ—è—Ö¬ª. –ú–∞—Ä–∏—è –ö—é—Ä–∏`,
		`¬´–ö–æ–≥–¥–∞ —è —Å–æ–±–∏—Ä–∞—é—Å—å –ø–∏—Å–∞—Ç—å –Ω–æ–≤—ã–π —Å—Ü–µ–Ω–∞—Ä–∏–π, —Å–∞–º–æ–µ —Ç—Ä—É–¥–Ω–æ–µ –¥–ª—è –º–µ–Ω—è ‚Äì —ç—Ç–æ –ø–æ–π—Ç–∏ –≤ –∫–∞–Ω—Ü—Ç–æ–≤–∞—Ä—ã –∏ –∫—É–ø–∏—Ç—å –±–ª–æ–∫–Ω–æ—Ç¬ª. –ö–≤–µ–Ω—Ç–∏–Ω –¢–∞—Ä–∞–Ω—Ç–∏–Ω–æ`,
		`¬´–ù–µ–Ω–∞–≤–∏–∂—É –≤—Å—è—á–µ—Å–∫—É—é –º–µ—Ä—Ç–≤–µ—á–∏–Ω—É! –û–±–æ–∂–∞—é –≤—Å—è—á–µ—Å–∫—É—é –∂–∏–∑–Ω—å!¬ª –í–ª–∞–¥–∏–º–∏—Ä –ú–∞—è–∫–æ–≤—Å–∫–∏–π`,
		`¬´–ú—ã—à–ª–µ–Ω–∏–µ ‚Äì –≤–µ—Ä—Ö –±–ª–∞–∂–µ–Ω—Å—Ç–≤–∞ –∏ —Ä–∞–¥–æ—Å—Ç—å –∂–∏–∑–Ω–∏, –¥–æ–±–ª–µ—Å—Ç–Ω–µ–π—à–µ–µ –∑–∞–Ω—è—Ç–∏–µ —á–µ–ª–æ–≤–µ–∫–∞¬ª. –ê—Ä–∏—Å—Ç–æ—Ç–µ–ª—å`,
		`¬´–£ —Ç–µ–±—è –µ—Å—Ç—å –≤—Ä–∞–≥–∏? –•–æ—Ä–æ—à–æ. –ó–Ω–∞—á–∏—Ç, –≤ —Å–≤–æ–µ–π –∂–∏–∑–Ω–∏ —Ç—ã —á—Ç–æ-—Ç–æ –∫–æ–≥–¥–∞-—Ç–æ –æ—Ç—Å—Ç–∞–∏–≤–∞–ª¬ª. –£–∏–Ω—Å—Ç–æ–Ω –ß–µ—Ä—á–∏–ª–ª—å`,
		`¬´–ö–æ–≥–¥–∞-–Ω–∏–±—É–¥—å –Ω–µ —Å—Ç—Ä–∞—à–Ω–æ —É–º–µ—Ä–µ—Ç—å ‚Äì —Å—Ç—Ä–∞—à–Ω–æ —É–º–µ—Ä–µ—Ç—å –≤–æ—Ç —Å–µ–π—á–∞—Å¬ª. –ê–ª–µ–∫—Å–∞–Ω–¥—Ä –°–æ–ª–∂–µ–Ω–∏—Ü—ã–Ω`,
		`¬´–Ø —Å–µ—Ä—å—ë–∑–Ω–æ –æ—Ç–Ω–æ—à—É—Å—å –∫ —Å–≤–æ–µ–π —Ä–∞–±–æ—Ç–µ, –∞ —ç—Ç–æ –≤–æ–∑–º–æ–∂–Ω–æ —Ç–æ–ª—å–∫–æ –ø—Ä–∏ –Ω–µ—Å–µ—Ä—å—ë–∑–Ω–æ–º –æ—Ç–Ω–æ—à–µ–Ω–∏–∏ –∫ —Å–æ–±—Å—Ç–≤–µ–Ω–Ω–æ–π –ø–µ—Ä—Å–æ–Ω–µ¬ª. –ê–ª–∞–Ω –†–∏–∫–º–∞–Ω`,
		`¬´–•–∞—Ä–∞–∫—Ç–µ—Ä ‚Äì —ç—Ç–æ –∏ –µ—Å—Ç—å —Å—É–¥—å–±–∞¬ª. –ú–∞–π—è –ü–ª–∏—Å–µ—Ü–∫–∞—è`,
		`¬´–í–Ω–∏–º–∞–π –ª–∏—à—å –æ–¥–Ω–æ–º—É —É—á–∏—Ç–µ–ª—é ‚Äì –ü—Ä–∏—Ä–æ–¥–µ¬ª. –†–µ–º–±—Ä–∞–Ω–¥—Ç`,
		`¬´–£—Å–ø–µ—Ö ‚Äì –ø–∞—Ä—à–∏–≤—ã–π —É—á–∏—Ç–µ–ª—å. –û–Ω –∑–∞—Å—Ç–∞–≤–ª—è–µ—Ç —É–º–Ω—ã—Ö –ª—é–¥–µ–π –¥—É–º–∞—Ç—å, —á—Ç–æ –æ–Ω–∏ –Ω–µ –º–æ–≥—É—Ç –ø—Ä–æ–∏–≥—Ä–∞—Ç—å¬ª. –ë–∏–ª–ª –ì–µ–π—Ç—Å`,
		`¬´–ß–µ–º–ø–∏–æ–Ω–∞–º–∏ —Å—Ç–∞–Ω–æ–≤—è—Ç—Å—è –Ω–µ –≤ —Ç—Ä–µ–Ω–∞–∂—ë—Ä–Ω—ã—Ö –∑–∞–ª–∞—Ö. –ß–µ–º–ø–∏–æ–Ω–∞ —Ä–æ–∂–¥–∞–µ—Ç —Ç–æ, —á—Ç–æ —É —á–µ–ª–æ–≤–µ–∫–∞ –≤–Ω—É—Ç—Ä–∏: –∂–µ–ª–∞–Ω–∏—è, –º–µ—á—Ç—ã, —Ü–µ–ª–∏¬ª. –ú—É—Ö–∞–º–º–µ–¥ –ê–ª–∏`,
		`¬´–õ—é–¥–∏ ‚Äì —Å–ª–∏—à–∫–æ–º —Å–ª–æ–∂–Ω—ã–µ —Å—É—â–µ—Å—Ç–≤–∞, —á—Ç–æ–±—ã –ø–æ–Ω—è—Ç—å –∏—Ö –ø–æ–ª–Ω–æ—Å—Ç—å—é¬ª. –¢–æ–º –•—ç–Ω–∫—Å`,
		`¬´–ü–µ—Ä—Å–ø–µ–∫—Ç–∏–≤–∞ —Ä–∞–Ω–æ —É–º–µ—Ä–µ—Ç—å –∑–∞—Å—Ç–∞–≤–∏–ª–∞ –º–µ–Ω—è –ø–æ–Ω—è—Ç—å, —á—Ç–æ –∂–∏–∑–Ω—å —Å—Ç–æ–∏—Ç —Ç–æ–≥–æ, —á—Ç–æ–±—ã –µ—ë –ø—Ä–æ–∂–∏—Ç—å¬ª. –°—Ç–∏–≤–µ–Ω –•–æ–∫–∏–Ω–≥`,
		`¬´–ù–µ —Ç–∞–∫ —É–∂ —Å–ª–æ–∂–Ω–æ –∏–∑–º–µ–Ω–∏—Ç—å –æ–±—â–µ—Å—Ç–≤–æ ‚Äì —Å–ª–æ–∂–Ω–æ –∏–∑–º–µ–Ω–∏—Ç—å —Å–µ–±—è¬ª. –ù–µ–ª—å—Å–æ–Ω –ú–∞–Ω–¥–µ–ª–∞`,
		`¬´–ù–µ–æ–±—Ö–æ–¥–∏–º–æ, —á—Ç–æ–±—ã —Ö—É–¥–æ–∂–Ω–∏–∫, –∫—Ä–æ–º–µ –≥–ª–∞–∑–∞, –≤–æ—Å–ø–∏—Ç—ã–≤–∞–ª –∏ —Å–≤–æ—é –¥—É—à—É¬ª. –í–∞—Å–∏–ª–∏–π –ö–∞–Ω–¥–∏–Ω—Å–∫–∏–π`,
		`¬´–Ø –¥—ã—à—É, –∏ –∑–Ω–∞—á–∏—Ç ‚Äì —è –ª—é–±–ª—é! –Ø –ª—é–±–ª—é, –∏ –∑–Ω–∞—á–∏—Ç ‚Äì —è –∂–∏–≤—É!¬ª –í–ª–∞–¥–∏–º–∏—Ä –í—ã—Å–æ—Ü–∫–∏–π`,
		`¬´–§–∞–Ω—Ç–∞–∑–∏—è –º—É–∂—á–∏–Ω—ã ‚Äì –ª—É—á—à–µ–µ –æ—Ä—É–∂–∏–µ –∂–µ–Ω—â–∏–Ω—ã¬ª. –°–æ—Ñ–∏ –õ–æ—Ä–µ–Ω`,
		`¬´–¢–æ, —á—Ç–æ –º—ã –∑–Ω–∞–µ–º, —ç—Ç–æ –∫–∞–ø–ª—è, –∞ —Ç–æ, —á—Ç–æ –º—ã –Ω–µ –∑–Ω–∞–µ–º, —ç—Ç–æ –æ–∫–µ–∞–Ω¬ª. –ò—Å–∞–∞–∫ –ù—å—é—Ç–æ–Ω`,
		`¬´–ù–∏ –≤—ã—Å–æ–∫–∏–π –∏–Ω—Ç–µ–ª–ª–µ–∫—Ç, –Ω–∏ –≤–æ–æ–±—Ä–∞–∂–µ–Ω–∏–µ, –Ω–∏ —Ç–æ –∏ –¥—Ä—É–≥–æ–µ –≤–º–µ—Å—Ç–µ –Ω–µ —Ç–≤–æ—Ä—è—Ç –≥–µ–Ω–∏—è. –õ—é–±–æ–≤—å, –ª—é–±–æ–≤—å –∏ –ª—é–±–æ–≤—å ‚Äì –≤–æ—Ç –≤ —á—ë–º —Å—É—â–Ω–æ—Å—Ç—å –≥–µ–Ω–∏—è¬ª. –í–æ–ª—å—Ñ–≥–∞–Ω–≥ –ê–º–∞–¥–µ–π –ú–æ—Ü–∞—Ä—Ç`,
		`¬´–û–ø—Ä–∞–≤–¥–∞–π—Ç–µ, –Ω–µ –∫–∞—Ä–∞–π—Ç–µ, –Ω–æ –Ω–∞–∑–æ–≤–∏—Ç–µ –∑–ª–æ –∑–ª–æ–º¬ª. –§—ë–¥–æ—Ä –î–æ—Å—Ç–æ–µ–≤—Å–∫–∏–π`,
		`¬´–ù–µ –æ–±–æ—Ä–∞—á–∏–≤–∞–µ—Ç—Å—è —Ç–æ—Ç, –∫—Ç–æ —É—Å—Ç—Ä–µ–º–ª—ë–Ω –∫ –∑–≤—ë–∑–¥–∞–º¬ª. –õ–µ–æ–Ω–∞—Ä–¥–æ –¥–∞ –í–∏–Ω—á–∏`,
		`¬´–ù–µ–Ω–∞–≤–∏–∂—É —Å–æ–≤–µ—Ç—ã ‚Äì –≤—Å–µ, –∫—Ä–æ–º–µ —Å–≤–æ–∏—Ö¬ª. –î–∂–µ–∫ –ù–∏–∫–æ–ª—Å–æ–Ω`,
		`¬´–ö—Ä–∞—Å–æ—Ç–∞ –∂–µ–Ω—â–∏–Ω—ã –º–Ω–æ–∂–∏—Ç—Å—è –≤–º–µ—Å—Ç–µ —Å –µ—ë –≥–æ–¥–∞–º–∏¬ª. –û–¥—Ä–∏ –•–µ–ø–±–µ—Ä–Ω`,
		`¬´–ß–µ–ª–æ–≤–µ–∫ –Ω–µ –∑–Ω–∞–µ—Ç, —á—Ç–æ –º–æ–∂–µ—Ç, –ø–æ–∫–∞ –Ω–µ –ø–æ–ø—ã—Ç–∞–µ—Ç—Å—è —ç—Ç–æ —Å–¥–µ–ª–∞—Ç—å¬ª. –£–∏–ª—å—è–º –®–µ–∫—Å–ø–∏—Ä`,
		`¬´–•–æ—Ä–æ—à–∏–µ –¥—Ä—É–∑—å—è –∑–Ω–∞—é—Ç, —á—Ç–æ –ª—É—á—à–µ –∑–∞–º–æ–ª—á–∞—Ç—å, –∫–æ–≥–¥–∞ –¥—Ä—É–≥–æ–≥–æ –∑–∞—Å—Ç–∞–≤–ª—è—é—Ç –≤—ã–π—Ç–∏ –∏–∑ —Å–µ–±—è¬ª. –ì–∞–ª–∏–ª–µ–æ –ì–∞–ª–∏–ª–µ–π`,
		`¬´–û–¥–Ω–∞ —Å–º–µ—à–Ω–∞—è –∏—Å—Ç–∏–Ω–∞ ‚Äì —Ç–æ, —á—Ç–æ —É–º –Ω–µ —Ç–æ–ª—å–∫–æ —Å–ª—É–∂–∏—Ç –Ω–∞–º, –Ω–æ –∏ –æ–±–º–∞–Ω—ã–≤–∞–µ—Ç –Ω–∞—Å¬ª. –†–µ–Ω–µ –î–µ–∫–∞—Ä—Ç`,
		`¬´–Ø –¥–æ–∫–∞–∑–∞–ª, —á—Ç–æ –Ω–µ –º–æ–∂–µ—Ç –±—ã—Ç—å –±–µ—Å–∫–æ–Ω–µ—á–Ω–æ—Å—Ç–∏ –≤ –º–∞—Ç–µ–º–∞—Ç–∏–∫–µ, –∞ —á—Ç–æ –∂–µ –º–Ω–µ –æ—Å—Ç–∞–ª–æ—Å—å?¬ª. –ì–µ–æ—Ä–≥ –ö–∞–Ω—Ç–æ—Ä`,
		`¬´–ù–∏–∫–æ–≥–¥–∞ –Ω–µ –≥–æ–≤–æ—Ä–∏ –Ω–∏–∫–æ–≥–¥–∞¬ª. –ß–∞—Ä–ª—å–∑ –î–∏–∫–∫–µ–Ω—Å`,
		`¬´–ß—Ç–æ–±—ã –ø–æ–±–µ–¥–∏—Ç—å –∑–ª–æ, –¥–æ—Å—Ç–∞—Ç–æ—á–Ω–æ –ø—Ä–æ—Å—Ç–æ –Ω–µ —É—á–∞—Å—Ç–≤–æ–≤–∞—Ç—å –≤ –Ω—ë–º¬ª. –ê–ª—å–±–µ—Ä—Ç –≠–π–Ω—à—Ç–µ–π–Ω`,
	}

	// –ì–µ–Ω–µ—Ä–∏—Ä—É–µ–º —Å–ª—É—á–∞–π–Ω–æ–µ —á–∏—Å–ª–æ, —á—Ç–æ–±—ã –≤—ã–±—Ä–∞—Ç—å –ø—Ä–µ–¥—Å–∫–∞–∑–∞–Ω–∏–µ
	randomIndex := rand.Intn(len(predictions))

	// –í—ã–≤–æ–¥–∏–º –ø—Ä–µ–¥—Å–∫–∞–∑–∞–Ω–∏–µ
	return predictions[randomIndex]
}
